import { Component } from '@angular/core';

@Component({
  selector: 'app-second',
  templateUrl: './second.component.html',
  styleUrls: ['./second.component.css']
})
export class SecondComponent {
  message1:string="Welcome to the Velocity";
  annualSalary:number = 45000;
  totalPrice:number=3412.1234;

  todaysDate = new Date();

}
<p>second works!</p>
<h3>Annual Salary {{annualSalary | currency}}</h3>
<h3>Annual Salary {{annualSalary | currency:'INR'}}</h3>
<h3>Today's Date: {{todaysDate}}</h3>
<h3>Todays Date with Pipe: {{todaysDate | date}}</h3>
<h3>Short Date : {{todaysDate | date:'shortDate'}}</h3>
<h3>Medium Date : {{todaysDate | date:'mediumDate'}}</h3>
<h3>Date in dd/mm/yyyy : {{todaysDate | date:'dd/MM/yyyy'}}</h3>

<h3>Total Price:{{totalPrice | number:'.1-2'}}</h3>

<h3>Total Price:{{totalPrice | number:'.1-2'}}</h3>
