import { Component } from '@angular/core';

@Component({
  selector: 'app-event-binding',
  templateUrl: './event-binding.component.html',
  styleUrls: ['./event-binding.component.css']
})
export class EventBindingComponent {
  count:number = 0;

  login(){
    console.log("Login Success");
  }

  addToCart(){
    ++this.count
  }

  removeFromCart(){
    --this.count;
  }

  performAction(actionName:string){
    if(actionName === 'Increment'){
      this.count++
    }else {
      this.count--;
    }
  }


  print(){
    console.log("mouse over");
  }

  enterdName:string="";

  lastName:string="";
  firstName:string="";
  cityName:string="Pune"; 

  stateName:string="Maharashtra";

  firstNameChanged(data:any){
    console.log("Change event fired ",data.target.value);
    //logic 
    this.firstName = data.target.value;

    this.enterdName = this.firstName + " " + this.lastName;
  }

  lastNameChanged(eventData:any){
   this.lastName = eventData.target.value ;
   console.log("Last Name",this.lastName);
   this.enterdName = this.firstName + " " + this.lastName;
  }


  cityNameChanged(eventData:any){
   this.cityName =  eventData.target.value ;
   console.log("cityName",this.cityName);
  }


  printStateName(){
    console.log("State Name",this.stateName);
  }
}
}
<p>event-binding works!</p>

<h2>Event Binding</h2>

<button id="id1" (click)="login()">Login</button>

<h4>Cart Count:{{count}}</h4>

<!-- <button (click)="addToCart()">Add To Cart</button> &nbsp;
<button (click)="removeFromCart()">Remove From Cart</button> -->

<button (click)="performAction('Increment')" class="margin">Add To Cart</button>
<button (click)="performAction('Decrement')">Remove From Cart</button>

<h1 (mouseover)="print()">Hover Me</h1>

<label for="">First Name</label>
<input type="text" name="fistName" (change)="firstNameChanged($event)">



<label for="">Last Name</label>
<input type="text" name="fistName" (input)="lastNameChanged($event)">

<h6>Entered Name is : {{enterdName}}</h6>

<!--  -->
<label for="">City Name</label>
<input type="text" name="city" [value]="cityName" (change)="cityNameChanged($event)" >


<label for="">State Name</label>
<input type="text" name="city" [(ngModel)]="stateName" (change)="printStateName()">
