import { Component } from '@angular/core';

@Component({
  selector: 'app-third',
  templateUrl: './third.component.html',
  styleUrls: ['./third.component.css']
})
export class ThirdComponent {
  productModal:ProductModel = new ProductModel();


 calculateTotalPrice(){
  console.log("product Data",this.productModal);

  this.productModal.totalPrice = (Number(this.productModal.price) * Number(this.productModal.quantity)) - Number(this.productModal.discount);
 }

 reset(){
  // this.productModal.productName = "";
  // this.productModal.price = 0 ;
  // this.productModal.quantity = 0 ;
  // this.productModal.totalPrice = 0 ;
  // this.productModal.discount = 0 ;
  this.productModal = new ProductModel();
 }


}

export class ProductModel {
   productName!:string;
   price!:number;
   quantity!:number;
   discount!:number;
   totalPrice!:number;
}
<p>third works!</p>
<div class="container home-container border shadow mt-2 p-2">
    <form>
        <div class="mb-3">
            <label for="id1" class="form-label">Product Name</label>
            <input type="text" class="form-control" id="id1" name="pName" [(ngModel)]="productModal.productName">
        </div>
        <div class="mb-3">
            <label for="id2" class="form-label">Price</label>
            <input type="text" class="form-control" id="id2" name="price" [(ngModel)]="productModal.price" (ngModelChange)="calculateTotalPrice()">
        </div>
        <div class="mb-3">
            <label for="id3" class="form-label">Quantity</label>
            <input type="text" class="form-control" id="id3" name="quantity" [(ngModel)]="productModal.quantity" (ngModelChange)="calculateTotalPrice()">
        </div>
        <div class="mb-3">
            <label for="id4" class="form-label">Discount</label>
            <input type="text" class="form-control" id="id4" name="discount" [(ngModel)]="productModal.discount" (ngModelChange)="calculateTotalPrice()">
        </div>
        <p class="fs-6 fw-bold">Total Price of The Product Is : {{productModal.totalPrice}} </p>
        <button type="submit" class="btn btn-primary mb-2" (click)="calculateTotalPrice()">Calculate</button>
        <button type="button" class="btn btn-primary mb-2 ms-2" (click)="reset()">Reset</button>

    </form>
</div>
