import { Component } from '@angular/core';

@Component({
  selector: 'app-directive-example',
  templateUrl: './directive-example.component.html',
  styleUrls: ['./directive-example.component.css']
})
export class DirectiveExampleComponent {
  isShow:boolean = true ;
  isDisplay:boolean = false ;

  toggle(){
      this.isDisplay = !this.isDisplay;
  }
}
<p>directive-example works!</p>

<h3 class="text-center">Directive Example</h3>
<div class="container w-50">
    <div class="mb-3" *ngIf="isShow">
        <label for="id1" class="form-label">User Name</label>
        <input type="text" class="form-control" id="id1" name="uName">
    </div>
</div>
<button type="button" (click)="toggle()">Toggle</button>
<button class="btn btn-secondary ms-2" *ngIf="!isDisplay">Sign Up</button>


<button class="btn btn-primary" *ngIf="isDisplay;">Login</button>
